import React, { Component } from 'react';

class Feedback extends Component {
  constructor() {
    super();
    this.state = {
      countRight: 0,
    };
    this.handleAssertions = this.handleAssertions.bind(this);
  }



  handleAssertions() {
    this.setState({
      countRight: assertions,
    });
  }

  render() {
    const justAssertions = localStorage.getItem('state');
    const { assertions } = justAssertions.player;

    const { countRight } = this.state;
    console.log(countRight);
    const TRES = 3;

    return (
      <div>
        <h2 data-testid="feedback-text">
        { countRight >= TRES ? 'Mandou bem!' : 'Podia ser melhor...' }
      </h2>
      </div>
    );
  }
}

export default Feedback;
